mapper.update:
update customer 
   set lastlog_date = sysdate
where customer_id = #{customerId}

update customer
    set customer_state = #{customerState}
    where customer_id = #{customerId}

update customer
	set customer_state = 2
	where customer_no = #{customerNo}

update customer
	set customer_state = 1
	where customer_no = #{customerNo}
