mapper.select:
select nvl(substr(cuso_order_date,4,2),'total') as orderMonth, sum(cuso_total_cost) as totalCost  from cusorder 
where cuso_order_state=2 and substr(cuso_order_date,1,2)=to_char(sysdate,'yy') and cuso_branch=#{branchName} 
group by rollup (cuso_branch,substr(cuso_order_date,4,2))

select * from (select rownum as rnum, b.* from (select nvl(buc_main,'total') as ingredients, sum(buc_cost*buc_quantity) as totalCost from bucket where substr(buc_reg_date,1,2)=to_char(sysdate,'yy')
and substr(buc_reg_date,4,2)=#{orderMonth}
group by rollup(buc_main) order by totalCost desc) b)

select * from (select rownum as rnum, b.* from (select nvl(buc_main,'total') as ingredients, sum(buc_cost*buc_quantity) as totalCost 
from bucket where substr(buc_reg_date,1,2)=to_char(sysdate,'yy') and substr(buc_reg_date,4,2)=


		#{orderMonth}


		to_char(sysdate,'mm')


 and buc_branch=#{cusoBranch} 
 group by rollup(buc_main) order by totalcost desc) b)
