mapper.select:
select count(*) cnt from m_stock_tbl mst, ingredients i where mst.ITEM_IDX=i.INGRE_IDX
and LOWER(i.ingre_label) like LOWER('%'||#{searchVal}||'%')
and i.ingre_type = #{searchVal}
and mst.M_ORDER_MANAGER_ID=#{mgrId}

select rnum, m_stock_idx as mStockIdx, m_stock as mStock, ingre_label as ingreLabel, ingre_unit as ingreUnit, ingre_filepath as ingreFilepath from ( select rownum rnum, s.* from (select * from m_stock_tbl mst, ingredients i where mst.ITEM_IDX=I.INGRE_IDX
and LOWER(i.ingre_label) like LOWER('%'||#{searchVal}||'%')
and i.ingre_type = #{searchVal}
and mst.M_ORDER_MANAGER_ID=#{mgrId}
order by 1 desc) s ) where rnum between #{start} and #{end}

mapper.update:
update
	m_stock_tbl
set
	m_stock = #{mItemAmount}
where
	m_stock_idx = #{mStockIdx}
